using Godot;
using System;

public partial class JsonExporter : Node
{
    // !
    [Export] Node players;
    [Export] RigidBody3D ball;

    public void DoTheThing() {
        Export(TeamPositions(1), TeamPositions(2), new Vector2(ball.Position.X, ball.Position.Z));
    }
    
    Vector2[] TeamPositions(int team) {
        int teamSize = players.GetChildCount() / 2;
        Vector2[] teamArray = new Vector2[teamSize];

        int j = 0;
        for (int i = 0; i < players.GetChildCount(); i++) {
            Player player = (Player)(players.GetChild(i)); // yeah bro because casting would totally save us from an error bro
            if (player.team == team) {
                teamArray[j] = new Vector2(player.Position.X, player.Position.Z);
            }
        }
        
        return teamArray;
    }

    void Export(Vector2[] team1Positions, Vector2[] team2Positions, Vector2 ballPosition) {
        // actually puts info into json
    }
}
